// Enhanced data processing with validation and enrichment
const items = $input.all();
const processedItems = [];
const errors = [];
const stats = { total: 0, valid: 0, invalid: 0, duplicates: 0 };
const seenUrls = new Set();

for (const item of items) {
  const data = item.json;
  stats.total++;

  try {
    // Validate required fields
    if (!data.workflowUrl || !data.title) {
      stats.invalid++;
      errors.push({ reason: 'Missing required fields', data });
      continue;
    }

    // Normalize and validate URL
    let url = data.workflowUrl.trim();
    if (url.startsWith('/')) {
      url = 'https://n8n.io' + url;
    }

    // Check for duplicates
    if (seenUrls.has(url)) {
      stats.duplicates++;
      continue;
    }
    seenUrls.add(url);

    // Enhanced data structure
    const processedItem = {
      // Core data
      url: url,
      title: data.title.trim(),
      category: data.category ? data.category.trim() : 'Uncategorized',
      description: data.description ? data.description.trim() : '',
      author: data.author ? data.author.trim() : 'Unknown',

      // Enhanced metadata
      tags: data.tags ? data.tags.split(',').map(t => t.trim()).filter(Boolean) : [],
      complexity: data.complexity || 'unknown',

      // Processing metadata
      extractedAt: new Date().toISOString(),
      source: 'n8n.io',
      processingId: `scrape_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,

      // Quality indicators
      hasDescription: !!data.description,
      hasAuthor: !!data.author,
      estimatedQuality: (
        (data.description ? 2 : 0) +
        (data.author ? 1 : 0) +
        (data.tags ? 1 : 0) +
        (data.category && data.category !== 'Uncategorized' ? 1 : 0)
      )
    };

    processedItems.push({ json: processedItem });
    stats.valid++;

  } catch (error) {
    stats.invalid++;
    errors.push({ reason: error.message, data });
  }
}

// Add processing summary
processedItems.push({
  json: {
    _processingStats: stats,
    _errors: errors,
    _timestamp: new Date().toISOString(),
    _type: 'processing_summary'
  }
});

console.log(`Processing complete: ${stats.valid}/${stats.total} valid items`);
return processedItems;

// Enhanced workflow analysis and validation
const items = $input.all();
const enhancedWorkflows = [];
const analysisErrors = [];

for (const item of items) {
  const data = item.json;

  try {
    // Parse workflow JSON if available
    let workflowData = null;
    let nodeAnalysis = null;

    if (data.workflowJson) {
      try {
        workflowData = JSON.parse(data.workflowJson);

        // Analyze workflow structure
        nodeAnalysis = {
          totalNodes: workflowData.nodes ? workflowData.nodes.length : 0,
          nodeTypes: workflowData.nodes ? [...new Set(workflowData.nodes.map(n => n.type))] : [],
          hasWebhook: workflowData.nodes ? workflowData.nodes.some(n => n.type.includes('webhook')) : false,
          hasSchedule: workflowData.nodes ? workflowData.nodes.some(n => n.type.includes('schedule')) : false,
          complexity: calculateComplexity(workflowData)
        };
      } catch (parseError) {
        analysisErrors.push({ url: data.url, error: 'JSON parse failed', details: parseError.message });
      }
    }

    // Enhanced workflow object
    const enhancedWorkflow = {
      ...data,

      // Workflow analysis
      analysis: nodeAnalysis,

      // Enhanced metadata
      nodeCount: parseInt(data.nodeCount) || (nodeAnalysis ? nodeAnalysis.totalNodes : 0),
      connectionCount: parseInt(data.connectionCount) || 0,
      downloadCount: parseInt(data.downloadCount) || 0,

      // Timestamps
      lastUpdated: data.lastUpdated || null,
      analyzedAt: new Date().toISOString(),

      // Quality scoring
      qualityScore: calculateQualityScore(data, nodeAnalysis),

      // Classification
      classification: classifyWorkflow(data, nodeAnalysis),

      // Raw workflow data (if available)
      rawWorkflow: workflowData
    };

    enhancedWorkflows.push({ json: enhancedWorkflow });

  } catch (error) {
    analysisErrors.push({ url: data.url, error: 'Analysis failed', details: error.message });
  }
}

// Helper functions
function calculateComplexity(workflow) {
  if (!workflow || !workflow.nodes) return 0;

  const nodeCount = workflow.nodes.length;
  const connectionCount = workflow.connections ? Object.keys(workflow.connections).length : 0;
  const uniqueNodeTypes = new Set(workflow.nodes.map(n => n.type)).size;

  // Simple complexity scoring
  return Math.min(10, Math.floor(
    (nodeCount * 0.3) + 
    (connectionCount * 0.4) + 
    (uniqueNodeTypes * 0.3)
  ));
}

function calculateQualityScore(data, analysis) {
  let score = 0;

  // Basic metadata quality
  if (data.description && data.description.length > 20) score += 2;
  if (data.author && data.author !== 'Unknown') score += 1;
  if (data.category && data.category !== 'Uncategorized') score += 1;

  // Workflow analysis quality
  if (analysis) {
    if (analysis.totalNodes > 3) score += 2;
    if (analysis.nodeTypes.length > 2) score += 1;
    if (analysis.hasWebhook || analysis.hasSchedule) score += 1;
  }

  // Usage indicators
  if (data.downloadCount > 10) score += 1;
  if (data.downloadCount > 100) score += 1;

  return Math.min(10, score);
}

function classifyWorkflow(data, analysis) {
  const categories = [];

  // Basic classification
  if (data.category) categories.push(data.category.toLowerCase());

  // Advanced classification based on analysis
  if (analysis) {
    if (analysis.hasWebhook) categories.push('webhook-triggered');
    if (analysis.hasSchedule) categories.push('scheduled');
    if (analysis.totalNodes > 10) categories.push('complex');
    if (analysis.nodeTypes.some(type => type.includes('database'))) categories.push('database');
    if (analysis.nodeTypes.some(type => type.includes('api'))) categories.push('api-integration');
  }

  return categories;
}

// Add analysis summary
enhancedWorkflows.push({
  json: {
    _analysisStats: {
      totalProcessed: enhancedWorkflows.length,
      errors: analysisErrors,
      timestamp: new Date().toISOString()
    },
    _type: 'analysis_summary'
  }
});

console.log(`Analysis complete: ${enhancedWorkflows.length - 1} workflows processed`);
return enhancedWorkflows;

