// Transform HubSpot contacts for Mixpanel processing
const contacts = [];

for (const item of $input.all()) {
  // Extract email value from HubSpot's nested structure
  const emailValue = item.json.properties?.email?.value || item.json.properties?.email;
  const firstnameValue = item.json.properties?.firstname?.value || item.json.properties?.firstname || '';
  const lastnameValue = item.json.properties?.lastname?.value || item.json.properties?.lastname || '';
  const lastModifiedValue = item.json.properties?.lastmodifieddate?.value || item.json.properties?.lastmodifieddate;

  const contact = {
    hubspot_id: item.json.id || item.json['canonical-vid'] || item.json.vid,
    customer_id: emailValue, // Use email as customer_id for Mixpanel integration
    email: emailValue,
    name: `${firstnameValue} ${lastnameValue}`.trim(),
    current_page_views: item.json.properties?.mixpanel_page_views?.value || 0,
    last_modified: lastModifiedValue
  };

  // Validate required fields - only need valid email
  if (contact.email && contact.email.includes('@')) {
    contacts.push(contact);
    console.log(`‚úÖ Added contact: ${contact.name} (${contact.email})`);
  } else {
    console.log(`‚ùå Skipped contact: Missing or invalid email for ID ${contact.hubspot_id}`);
  }
}

console.log(`Processing ${contacts.length} valid contacts out of ${$input.all().length} total`);
return contacts.map(contact => ({ json: contact }));

// Optimized auth test - only test once per batch instead of per contact
const items = $input.all();
const firstItem = items[0];

// Test Mixpanel connection with a lightweight request
try {
  const testResponse = await $http.request({
    method: 'GET',
    url: 'https://mixpanel.com/api/2.0/engage',
    headers: {
      'Authorization': 'Basic ' + Buffer.from(`${$credentials.httpBasicAuth}:`).toString('base64')
    },
    params: {
      distinct_id: firstItem.json.customer_id,
      limit: 1
    }
  });

  console.log('‚úÖ Mixpanel authentication successful');

  // Return all items with auth confirmation
  return items.map(item => ({
    json: {
      ...item.json,
      mixpanel_auth_status: 'success',
      auth_tested_at: new Date().toISOString()
    }
  }));

} catch (error) {
  console.error('‚ùå Mixpanel authentication failed:', error.message);

  // Return items with error status
  return items.map(item => ({
    json: {
      ...item.json,
      mixpanel_auth_status: 'failed',
      auth_error: error.message,
      auth_tested_at: new Date().toISOString()
    }
  }));
}

// Process Mixpanel events and calculate page view metrics
const results = [];

for (const item of $input.all()) {
  const customer = item.json;
  const mixpanelResponse = customer.mixpanel_response || item.json;

  let pageViews = 0;
  let uniquePages = new Set();
  let lastPageView = null;

  try {
    // Handle different response formats
    if (typeof mixpanelResponse === 'string') {
      // Parse newline-delimited JSON
      const events = mixpanelResponse.split('\n')
        .filter(line => line.trim())
        .map(line => JSON.parse(line))
        .filter(event => event.event === 'Page View');

      pageViews = events.length;

      events.forEach(event => {
        if (event.properties && event.properties.page_url) {
          uniquePages.add(event.properties.page_url);
        }

        const eventTime = event.properties.time || event.properties.$time;
        if (eventTime && (!lastPageView || eventTime > lastPageView)) {
          lastPageView = eventTime;
        }
      });
    } else if (Array.isArray(mixpanelResponse)) {
      // Handle array response
      const pageViewEvents = mixpanelResponse.filter(event => event.event === 'Page View');
      pageViews = pageViewEvents.length;

      pageViewEvents.forEach(event => {
        if (event.properties && event.properties.page_url) {
          uniquePages.add(event.properties.page_url);
        }

        const eventTime = event.properties.time || event.properties.$time;
        if (eventTime && (!lastPageView || eventTime > lastPageView)) {
          lastPageView = eventTime;
        }
      });
    }

  } catch (error) {
    console.error(`Error processing events for ${customer.customer_id}:`, error);
    // Continue with default values
  }

  results.push({
    json: {
      hubspot_id: customer.hubspot_id,
      customer_id: customer.customer_id,
      email: customer.email,
      page_views_30d: pageViews,
      unique_pages_30d: uniquePages.size,
      last_page_view: lastPageView,
      previous_page_views: customer.current_page_views || 0,
      page_view_change: pageViews - (customer.current_page_views || 0),
      processed_at: new Date().toISOString()
    }
  });
}

console.log(`Processed page view data for ${results.length} customers`);
return results;

// Log successful updates and generate summary
const updates = $input.all();
const summary = {
  total_contacts_processed: updates.length,
  successful_updates: updates.filter(item => !item.json.error).length,
  failed_updates: updates.filter(item => item.json.error).length,
  total_page_views: updates.reduce((sum, item) => sum + (item.json.page_views_30d || 0), 0),
  execution_time: new Date().toISOString()
};

console.log('üéâ HubSpot ‚Üí Mixpanel Integration Summary:', JSON.stringify(summary, null, 2));

// Log any errors
updates.filter(item => item.json.error).forEach(item => {
  console.error(`‚ùå Failed to update contact ${item.json.customer_id}:`, item.json.error);
});

return [{ json: summary }];

// Handle and log errors from the workflow with enhanced error detection
const inputData = $input.first()?.json || {};

// Determine specific error type based on data structure
let errorMessage = 'Unknown error';
let errorType = 'Workflow Error';

// Check if this is a contact missing mixpanel_customer_id (from filter failure)
if (inputData.properties && !inputData.properties.mixpanel_customer_id) {
  errorMessage = 'Contact missing mixpanel_customer_id field - filtered out as expected';
  errorType = 'Filter Exclusion';
} 
// Check for authentication failures
else if (inputData.mixpanel_auth_status === 'failed' || inputData.auth_error) {
  errorMessage = inputData.auth_error || 'Mixpanel authentication failed';
  errorType = 'Authentication Failed';
}
// Check for other specific error types
else if (inputData.error?.message) {
  errorMessage = inputData.error.message;
  errorType = 'Node Error';
}

const errorInfo = {
  workflow_name: 'HubSpot ‚Üí Mixpanel Integration',
  error_time: new Date().toISOString(),
  error_node: 'Error Handler',
  error_message: errorMessage,
  error_type: errorType,
  contact_info: {
    email: inputData.properties?.email?.value || inputData.email || 'N/A',
    name: `${inputData.properties?.firstname?.value || ''} ${inputData.properties?.lastname?.value || ''}`.trim() || 'N/A',
    hubspot_id: inputData.vid || inputData['canonical-vid'] || inputData.id || 'N/A'
  },
  input_data: inputData,
  node_count: $input.all().length
};

// Enhanced logging based on error type
if (errorType === 'Filter Exclusion') {
  console.log(`‚ÑπÔ∏è Contact filtered out: ${errorInfo.contact_info.name} (${errorInfo.contact_info.email}) - missing mixpanel_customer_id field`);
} else if (errorType === 'Authentication Failed') {
  console.error('üîë Authentication failure detected - check Mixpanel credentials');
  console.error('‚ùå Workflow Error:', JSON.stringify(errorInfo, null, 2));
} else {
  console.error('‚ö†Ô∏è Workflow error detected');
  console.error('‚ùå Workflow Error:', JSON.stringify(errorInfo, null, 2));
}

return [{ json: errorInfo }];
