// More robustly transform HubSpot contacts for Mixpanel processing
const contacts = [];

for (const item of $input.all()) {
  const json = item.json || {};
  const properties = json.properties || {};

  // Extract email value from HubSpot's nested structure with extra safety
  const emailValue = properties.email?.value || properties.email || null;
  const firstnameValue = properties.firstname?.value || properties.firstname || '';
  const lastnameValue = properties.lastname?.value || properties.lastname || '';
  const lastModifiedValue = properties.lastmodifieddate?.value || properties.lastmodifieddate;

  const contact = {
    hubspot_id: json.id || json['canonical-vid'] || json.vid,
    customer_id: emailValue, // Use email as customer_id for Mixpanel integration
    email: emailValue,
    name: `${firstnameValue} ${lastnameValue}`.trim(),
    current_page_views: properties.mixpanel_page_views?.value ?? 0,
    last_modified: lastModifiedValue
  };

  // Validate required fields - only need valid email
  if (contact.email && contact.email.includes('@')) {
    contacts.push(contact);
    // console.log(`‚úÖ Added contact: ${contact.name} (${contact.email})`);
  } else {
    console.warn(`‚ùå Skipped contact: Missing or invalid email for HubSpot ID ${contact.hubspot_id}`);
  }
}

console.log(`Processing ${contacts.length} valid contacts out of ${$input.all().length} total`);
return contacts.map(contact => ({ json: contact }));

// Optimized auth test - only test once per batch instead of per contact
const items = $input.all();
const firstItem = items[0];

// Test Mixpanel connection with a lightweight request
try {
  const credentials = await this.getCredentials('Mixpanel_Credentials');
  const apiKey = credentials.user; // Mixpanel API Secret is stored in the 'user' field for Basic Auth
  const encodedAuth = Buffer.from(`${apiKey}:`).toString('base64');

  const testResponse = await $http.request({
    method: 'GET',
    url: 'https://mixpanel.com/api/2.0/engage',
    headers: {
      'Authorization': 'Basic ' + encodedAuth
    },
    params: {
      distinct_id: firstItem.json.customer_id,
      limit: 1
    }
  });

  console.log('‚úÖ Mixpanel authentication successful');

  // Return all items with auth confirmation
  return items.map(item => ({
    json: {
      ...item.json,
      mixpanel_auth_status: 'success',
      auth_tested_at: new Date().toISOString()
    }
  }));

} catch (error) {
  console.error('‚ùå Mixpanel authentication failed:', error.message);

  // Return items with error status
  return items.map(item => ({
    json: {
      ...item.json,
      mixpanel_auth_status: 'failed',
      auth_error: error.message,
      auth_tested_at: new Date().toISOString()
    }
  }));
}

const batch = $input.all();
const customerIds = batch.map(item => item.json.customer_id);

// Return the IDs for the API call and the original batch data for merging later
return [{
  json: {
    customer_ids: customerIds,
    original_batch: batch.map(item => item.json)
  }
}];

// Process Mixpanel events for the entire batch and calculate page view metrics
const inputData = $input.first().json;
const originalBatch = inputData.original_batch;
const mixpanelResponse = inputData.body;
const eventName = $node["Workflow Config"].json.mixpanelEventName || 'Page View';

let allEvents = [];
try {
  if (typeof mixpanelResponse === 'string' && mixpanelResponse.trim()) {
    allEvents = mixpanelResponse.split('\n').filter(line => line.trim()).map(line => JSON.parse(line));
  } else if (Array.isArray(mixpanelResponse)) {
    allEvents = mixpanelResponse;
  }
} catch (error) {
  console.error(`Error parsing Mixpanel response for the batch:`, error.message);
}

// Group events by distinct_id for efficient lookup
const eventsByCustomerId = allEvents.reduce((acc, event) => {
  const distinctId = event.properties?.distinct_id;
  if (distinctId) {
    if (!acc[distinctId]) {
      acc[distinctId] = [];
    }
    acc[distinctId].push(event);
  }
  return acc;
}, {});

const results = [];

for (const customer of originalBatch) {
  const customerId = customer.customer_id;
  const customerEvents = eventsByCustomerId[customerId] || [];

  const pageViewEvents = customerEvents.filter(event => event.event === eventName);
  const pageViews = pageViewEvents.length;

  const uniquePages = new Set();
  let lastPageView = null;

  pageViewEvents.forEach(event => {
    const props = event.properties || {};
    if (props.page_url) {
      uniquePages.add(props.page_url);
    }
    const eventTime = props.time || props.$time;
    if (eventTime && (!lastPageView || eventTime > lastPageView)) {
      lastPageView = eventTime;
    }
  });

  results.push({
    hubspot_id: customer.hubspot_id,
    customer_id: customerId,
    email: customer.email,
    page_views_30d: pageViews,
    unique_pages_30d: uniquePages.size,
    last_page_view: lastPageView,
    previous_page_views: customer.current_page_views || 0,
    page_view_change: pageViews - (customer.current_page_views || 0),
    processed_at: new Date().toISOString()
  });
}

console.log(`Processed page view data for ${results.length} customers in the batch`);
return results.map(res => ({ json: res }));

// Log successful updates and generate summary
const updates = $input.all();
const summary = {
  total_contacts_processed: updates.length,
  successful_updates: updates.filter(item => !item.json.error).length,
  failed_updates: updates.filter(item => item.json.error).length,
  total_page_views: updates.reduce((sum, item) => sum + (item.json.page_views_30d || 0), 0),
  execution_time: new Date().toISOString()
};

console.log('üéâ HubSpot ‚Üí Mixpanel Integration Summary:', JSON.stringify(summary, null, 2));

// Log any errors
updates.filter(item => item.json.error).forEach(item => {
  console.error(`‚ùå Failed to update contact ${item.json.customer_id}:`, item.json.error);
});

return [{ json: summary }];

// Handle and log errors from the workflow with enhanced error detection and context
const error = $input.first();
const inputData = error.json || {};

// Determine specific error type based on data structure
let errorMessage = 'Unknown error occurred in the workflow.';
let errorType = 'GenericWorkflowError';
let suggestion = 'Check the workflow execution logs for details.';

// Check for filter failure (no mixpanel_customer_id)
if (inputData.properties && !inputData.properties.mixpanel_customer_id) {
  errorMessage = `Contact with HubSpot ID ${inputData.id || inputData.vid} was filtered out as expected because the 'mixpanel_customer_id' field is empty.`;
  errorType = 'FilterExclusion';
  suggestion = 'This is usually normal. Ensure the contact has a `mixpanel_customer_id` in HubSpot if they should be processed.';
} 
// Check for authentication failures from the dedicated test node
else if (inputData.mixpanel_auth_status === 'failed' || inputData.auth_error) {
  errorMessage = inputData.auth_error || 'Mixpanel authentication failed during pre-flight check.';
  errorType = 'AuthenticationFailed';
  suggestion = 'Verify that your Mixpanel credentials are correct and have the necessary permissions.';
}
// Check for HTTP request failures
else if (inputData.error && inputData.error.code) {
    errorMessage = `An API error occurred: ${inputData.error.message || 'No message'}. Status Code: ${inputData.error.code}`;
    errorType = 'ApiError';
    suggestion = `Check the API documentation for status code ${inputData.error.code} and review the request sent from the failing node.`
}
// Check for other code execution errors
else if (error.error) {
  errorMessage = error.error.message;
  errorType = 'NodeExecutionError';
  suggestion = `A code error occurred in node '${error.execution.node.name}'. Check the node's code and input data.`;
}

const errorInfo = {
  workflow: {
    name: 'HubSpot ‚Üí Mixpanel Integration',
    id: '{{$workflow.id}}',
    executionId: '{{$execution.id}}'
  },
  error: {
    type: errorType,
    message: errorMessage,
    suggestion: suggestion,
    timestamp: new Date().toISOString(),
    node: error.execution ? {
      name: error.execution.node.name,
      type: error.execution.node.type,
      id: error.execution.node.id
    } : { name: 'Unknown' }
  },
  context: {
      hubspotId: inputData.id || inputData.vid || 'N/A',
      email: inputData.properties?.email || inputData.email || 'N/A'
  },
  fullError: error
};

// More readable console output
console.error(`
üö® WORKFLOW ERROR: ${errorInfo.error.type}\n--------------------------------------------------\n- Message: ${errorInfo.error.message}\n- Suggestion: ${errorInfo.error.suggestion}\n- Node: ${errorInfo.error.node.name} (${errorInfo.error.node.type})\n- HubSpot ID: ${errorInfo.context.hubspotId}\n- Email: ${errorInfo.context.email}\n- Timestamp: ${errorInfo.error.timestamp}\n--------------------------------------------------\n`);

return [{ json: errorInfo }];
