// n8n.io Test - Detailed Debug Output
const response = $input.first().json;

console.log('=== N8N.IO TEST RESULTS ===');
console.log('Status Code:', response.statusCode || response.status || 'UNKNOWN');
console.log('Headers Present:', !!response.headers);
console.log('Body Present:', !!response.body);
console.log('Body Size:', response.body ? response.body.length : 0, 'characters');
console.log('Response Keys:', Object.keys(response));

if (response.statusCode === 200 || response.status === 200) {
  console.log('‚úÖ SUCCESS: n8n.io request worked!');
  console.log('Content Type:', response.headers ? response.headers['content-type'] : 'Not available');
  console.log('Server:', response.headers ? response.headers.server : 'Not available');

  if (response.body && response.body.includes('workflow')) {
    console.log('‚úÖ Body contains workflow content - scraping should work!');
  } else {
    console.log('‚ö†Ô∏è Body does not contain expected workflow content');
  }
} else {
  console.log('‚ùå FAILED: n8n.io request failed');
  console.log('Error Code:', response.statusCode || response.status || 0);
  console.log('Full Response:', JSON.stringify(response, null, 2));
}

return [{ json: {
  testResult: (response.statusCode === 200 || response.status === 200) ? 'SUCCESS' : 'FAILED',
  statusCode: response.statusCode || response.status,
  bodySize: response.body ? response.body.length : 0,
  timestamp: new Date().toISOString(),
  debugInfo: {
    keys: Object.keys(response),
    hasBody: !!response.body,
    hasHeaders: !!response.headers
  }
}}];
