// ===== COMPREHENSIVE HTTP DIAGNOSTIC ANALYSIS =====
const diagnosticData = $('ğŸ“‹ Initialize Diagnostics').first().json;
const session = diagnosticData.diagnostic_session;

console.log('ğŸ”§ ===== LOCAL N8N HTTP CLIENT DIAGNOSTICS =====');
console.log(`ğŸ“‹ Session: ${session}`);
console.log(`ğŸ”§ n8n Version: ${diagnosticData.n8n_version}`);
console.log(`âš™ï¸  Node.js Version: ${diagnosticData.node_version}`);
console.log(`ğŸ“¦ Installation: ${diagnosticData.installation_type}`);
console.log('');

// Collect all test results
const tests = [
  { name: 'HTTP Basic', node: 'ğŸŒ Test HTTP (Basic)' },
  { name: 'HTTPS Basic', node: 'ğŸ”’ Test HTTPS (Basic)' },
  { name: 'HTTPS Enhanced', node: 'ğŸš€ Test HTTPS (Enhanced)' }
];

const results = [];
const diagnosticSummary = {
  session,
  timestamp: new Date().toISOString(),
  environment: {
    n8nVersion: diagnosticData.n8n_version,
    nodeVersion: diagnosticData.node_version,
    installation: diagnosticData.installation_type,
    platform: 'macOS'
  },
  testResults: {},
  recommendations: [],
  criticalIssues: []
};

for (const test of tests) {
  try {
    const testResult = $(test.node).first().json;
    const success = testResult.statusCode === 200;

    results.push({
      test: test.name,
      success,
      statusCode: testResult.statusCode || 0,
      responseSize: testResult.body ? testResult.body.length : 0,
      hasHeaders: !!testResult.headers
    });

    diagnosticSummary.testResults[test.name] = {
      success,
      statusCode: testResult.statusCode || 0,
      error: success ? null : 'Network connection failed'
    };

    console.log(`${success ? 'âœ…' : 'âŒ'} ${test.name}: ${success ? 'SUCCESS' : 'FAILED'} (Status: ${testResult.statusCode || 'NO_RESPONSE'})`);

  } catch (error) {
    results.push({
      test: test.name,
      success: false,
      statusCode: 0,
      responseSize: 0,
      hasHeaders: false,
      error: error.message
    });

    diagnosticSummary.testResults[test.name] = {
      success: false,
      statusCode: 0,
      error: `Test execution failed: ${error.message}`
    };

    console.log(`âŒ ${test.name}: EXECUTION_ERROR (${error.message})`);
  }
}

console.log('');

// Analyze results and provide recommendations
const successfulTests = results.filter(r => r.success).length;
const totalTests = results.length;

console.log(`ğŸ“Š Test Summary: ${successfulTests}/${totalTests} tests passed`);

if (successfulTests === 0) {
  console.log('ğŸš¨ CRITICAL: All HTTP tests failed - fundamental HTTP client issue');
  diagnosticSummary.criticalIssues.push('Complete HTTP client failure');
  diagnosticSummary.recommendations.push('Check n8n installation integrity');
  diagnosticSummary.recommendations.push('Verify Node.js HTTP module compatibility');
  diagnosticSummary.recommendations.push('Consider reinstalling n8n via npm instead of Homebrew');
  diagnosticSummary.recommendations.push('Check for firewall/antivirus blocking n8n HTTP requests');
} else if (successfulTests < totalTests) {
  console.log('âš ï¸  PARTIAL: Some HTTP tests failed - configuration issue');
  diagnosticSummary.recommendations.push('Check SSL/TLS certificate configuration');
  diagnosticSummary.recommendations.push('Verify HTTP vs HTTPS handling differences');
} else {
  console.log('âœ… SUCCESS: All HTTP tests passed - HTTP client is working');
  diagnosticSummary.recommendations.push('HTTP client is functional - issue may be site-specific');
}

console.log('');
console.log('ğŸ”§ RECOMMENDED ACTIONS:');
diagnosticSummary.recommendations.forEach((rec, index) => {
  console.log(`   ${index + 1}. ${rec}`);
});

console.log('');
console.log('ğŸ“‹ DETAILED DIAGNOSTIC DATA:');
console.log(JSON.stringify(diagnosticSummary, null, 2));

return [{ json: {
  diagnosticSummary,
  testResults: results,
  nextSteps: [
    'Copy diagnostic data above',
    'Share with AI assistant for analysis',
    'Apply recommended fixes based on test results'
  ]
}}];
