  statusCode: httpResponse.statusCode || 0,
  isSuccess: httpResponse.statusCode === 200,
  hasBody: !!httpResponse.body,
  bodySize: httpResponse.body ? httpResponse.body.length : 0,
  contentType: (httpResponse.headers && httpResponse.headers['content-type']) ? httpResponse.headers['content-type'] : 'unknown',
  executionTime: Date.now() - stepStartTime,
  networkError: !httpResponse.statusCode || httpResponse.statusCode === 0
};

console.log(`âš¡ Execution Time: ${validationResults.executionTime}ms`);

if (validationResults.isSuccess) {
  console.log('âœ… HTTP Request: SUCCESS');
  console.log(`   Content Type: ${validationResults.contentType}`);
  console.log(`   Body Size: ${validationResults.bodySize} characters`);
} else if (validationResults.networkError) {
  console.log('ðŸš¨ HTTP Request: NETWORK ERROR');
  console.log(`   Status: No response received (likely network/connectivity issue)`);
  console.log(`   Troubleshooting: Check internet connection, DNS resolution, and firewall settings`);
} else {
  console.log('âŒ HTTP Request: FAILED');
  console.log(`   Error Details: Status ${validationResults.statusCode}`);
  console.log(`   Troubleshooting: Check network connectivity and target site availability`);
}

console.log('');

// Always return enriched data with testSessionId, even on network errors
const enrichedResponse = {
  // Preserve original HTTP response data (may be empty on network error)
  ...httpResponse,
  // Always include our enriched metadata
  testSessionId,
  step: 'http_fetch',
  validationResults,
  timestamp: new Date().toISOString(),
  // Add error context for network issues
  errorContext: validationResults.networkError ? {
