return [{ json: enrichedResponse }];

// ===== DATA EXTRACTION VALIDATION & LOGGING =====
const httpValidatorData = $('ðŸ” HTTP Response Validator').first().json;
const testSessionId = httpValidatorData.testSessionId;
const stepStartTime = Date.now();
const extractedData = $input.all();

console.log('ðŸ” ===== DATA EXTRACTION VALIDATION =====');
