// Analyze extraction quality
let extractionStats = {
  totalItems: extractedData.length,
  validItems: 0,
  invalidItems: 0,
  fieldCompleteness: {
    title: 0,
    description: 0,
    links: 0
  },
  executionTime: 0
};

// Detailed analysis of each extracted item
for (const item of extractedData) {
  const data = item.json;

  if (data.title || data.description || data.links) {
    extractionStats.validItems++;
  } else {
    extractionStats.invalidItems++;
  }

  // Count field completeness
  if (data.title) extractionStats.fieldCompleteness.title++;
  if (data.description) extractionStats.fieldCompleteness.description++;
  if (data.links) extractionStats.fieldCompleteness.links++;
}

extractionStats.executionTime = Date.now() - stepStartTime;

console.log(`   Valid Items: ${extractionStats.validItems}`);
console.log(`   Invalid Items: ${extractionStats.invalidItems}`);
console.log(`   Success Rate: ${extractionStats.totalItems > 0 ? ((extractionStats.validItems / extractionStats.totalItems) * 100).toFixed(2) : 0}%`);
console.log(`âš¡ Execution Time: ${extractionStats.executionTime}ms`);

console.log('ðŸ“ˆ Field Completeness:');
for (const [field, count] of Object.entries(extractionStats.fieldCompleteness)) {
  const percentage = extractionStats.totalItems > 0 ? ((count / extractionStats.totalItems) * 100).toFixed(1) : 0;
  console.log(`   ${field}: ${count}/${extractionStats.totalItems} (${percentage}%)`);
}

if (extractionStats.validItems === 0) {
  console.log('âŒ EXTRACTION FAILED: No valid items extracted');
  console.log('ðŸ”§ Troubleshooting:');
  console.log('   - Check CSS selectors for changes in website structure');
  console.log('   - Verify target website is accessible');
  console.log('   - Review HTML extraction configuration');
} else {
  console.log('âœ… EXTRACTION SUCCESS: Valid data extracted');
}

console.log('');

// Enhance data with test metadata
const enhancedData = extractedData.map((item, index) => ({
  json: {
    ...item.json,
    testSessionId,
    extractionIndex: index,
    extractionStats,
    timestamp: new Date().toISOString()
