return enhancedData;

// Enhanced workflow processing with comprehensive testing validation
const extractionData = $('ðŸ“Š Extraction Validator').first().json;
const testSessionId = extractionData.testSessionId;
const stepStartTime = Date.now();
const items = $input.all();
const processedItems = [];
const errors = [];
const stats = { total: 0, valid: 0, invalid: 0, duplicates: 0 };
const seenContent = new Set();

console.log('âš™ï¸ ===== DATA PROCESSING & ENHANCEMENT =====');
console.log(`ðŸ“‹ Session: ${testSessionId}`);
console.log(`ðŸŽ¯ Processing ${items.length} extracted items`);

for (const item of items) {
  const data = item.json;
  stats.total++;

  try {
    // Validate required fields
    if (!data.title && !data.description && !data.links) {
      stats.invalid++;
      errors.push({ reason: 'No extractable content found', data: { title: data.title, description: data.description, links: data.links } });
      console.log(`âŒ Invalid Item ${stats.total}: No extractable content`);
      continue;
    }

    // Create content signature for duplicate detection
    const contentSignature = `${data.title || ''}_${data.description || ''}_${data.links || ''}`;

    // Check for duplicates
    if (seenContent.has(contentSignature)) {
      stats.duplicates++;
      console.log(`âš ï¸  Duplicate content detected`);
      continue;
    }
    seenContent.add(contentSignature);

    // Enhanced data structure
    const processedItem = {
      // Core data
      title: data.title ? data.title.trim() : 'No title',
      description: data.description ? data.description.trim() : 'No description',
      links: data.links || 'No links',

      // Test metadata
      testSessionId,
      extractedAt: new Date().toISOString(),
      source: 'httpbin.org',
      processingId: `test_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,

      // Quality indicators
      hasTitle: !!data.title,
      hasDescription: !!data.description,
      hasLinks: !!data.links,
      estimatedQuality: (
        (data.title ? 2 : 0) +
        (data.description ? 2 : 0) +
        (data.links ? 1 : 0)
      )
    };

    processedItems.push({ json: processedItem });
    stats.valid++;

    console.log(`âœ… Processed Item ${stats.valid}: ${processedItem.title.substring(0, 50)}...`);

  } catch (error) {
    stats.invalid++;
    errors.push({ reason: error.message, data });
    console.log(`âŒ Processing Error ${stats.invalid}: ${error.message}`);
  }
}

const processingTime = Date.now() - stepStartTime;
const successRate = stats.total > 0 ? ((stats.valid / stats.total) * 100).toFixed(2) : 0;

console.log(`ðŸ“Š Processing Summary:`);
console.log(`   Total Items: ${stats.total}`);
console.log(`   Valid Items: ${stats.valid}`);
console.log(`   Invalid Items: ${stats.invalid}`);
console.log(`   Duplicates: ${stats.duplicates}`);
console.log(`   Success Rate: ${successRate}%`);
console.log(`âš¡ Processing Time: ${processingTime}ms`);

if (errors.length > 0) {
  console.log(`âš ï¸  Errors Encountered:`);
  errors.forEach((error, index) => {
    console.log(`   ${index + 1}. ${error.reason}`);
  });
}

console.log('');

// Add processing summary
processedItems.push({
  json: {
    _processingStats: stats,
    _errors: errors,
    _timestamp: new Date().toISOString(),
    _type: 'processing_summary',
    _testSessionId: testSessionId,
    _processingTime: processingTime,
    _successRate: successRate
  }
});

console.log(`âœ… Data processing completed: ${stats.valid}/${stats.total} valid items`);
return processedItems;

