// ===== ERROR HANDLING & DIAGNOSTIC REPORTING =====
const enrichedResponse = $input.first().json;
const testSessionId = enrichedResponse.testSessionId || 'unknown';
const validationResults = enrichedResponse.validationResults || {};
const stepStartTime = Date.now();

console.log('âŒ ===== ERROR HANDLING ACTIVATED =====');
console.log(`ðŸ“‹ Session: ${testSessionId}`);
console.log(`ðŸš¨ HTTP Request Failed`);
console.log(`ðŸ“Š Error Details:`);
console.log(`   Status Code: ${enrichedResponse.statusCode || validationResults.statusCode || 'UNKNOWN'}`);
console.log(`   Content Type: ${validationResults.contentType || 'unknown'}`);
