console.log(`   Has Body: ${validationResults.hasBody ? 'YES' : 'NO'}`);
console.log(`   URL: ${enrichedResponse.url || 'https://httpbin.org/html'}`);
console.log(`   Execution Time: ${validationResults.executionTime || 0}ms`);

// Enhanced error analysis using validation results
const statusCode = enrichedResponse.statusCode || validationResults.statusCode || 0;
const errorAnalysis = {
  statusCode: statusCode,
  category: getErrorCategory(statusCode),
  severity: getErrorSeverity(statusCode),
  recommendations: getRecommendations(statusCode),
  validationContext: {
    hasValidationData: !!validationResults,
    responseSize: validationResults.bodySize || 0,
    contentType: validationResults.contentType || 'unknown',
    executionTime: validationResults.executionTime || 0
  }
};

console.log(`ðŸ” Enhanced Error Analysis:`);
console.log(`   Category: ${errorAnalysis.category}`);
console.log(`   Severity: ${errorAnalysis.severity}`);
console.log(`   Validation Data Available: ${errorAnalysis.validationContext.hasValidationData ? 'YES' : 'NO'}`);
if (errorAnalysis.validationContext.hasValidationData) {
  console.log(`   Response Analysis: ${errorAnalysis.validationContext.responseSize} chars, ${errorAnalysis.validationContext.contentType}`);
}

console.log(`ðŸ”§ Recommended Actions:`);
errorAnalysis.recommendations.forEach((rec, index) => {
  console.log(`   ${index + 1}. ${rec}`);
