console.log('');

function getErrorCategory(statusCode) {
  if (statusCode >= 400 && statusCode < 500) return 'Client Error';
  if (statusCode >= 500) return 'Server Error';
  if (statusCode >= 300 && statusCode < 400) return 'Redirect Issue';
  if (statusCode === 0) return 'Network Error';
  return 'Unknown Error';
}

function getErrorSeverity(statusCode) {
  if ([404, 403, 401].includes(statusCode)) return 'HIGH';
  if ([500, 502, 503].includes(statusCode)) return 'CRITICAL';
  if ([429, 408].includes(statusCode)) return 'MEDIUM';
  if (statusCode === 0) return 'CRITICAL';
  return 'MEDIUM';
}

function getRecommendations(statusCode) {
  const recommendations = {
    0: ['Check network connectivity', 'Verify DNS resolution', 'Check firewall settings', 'Increase request timeout'],
    404: ['Check if the target URL is correct', 'Verify the website structure has not changed'],
    403: ['Check if access is blocked', 'Consider using different headers or user agent'],
    401: ['Authentication may be required', 'Check if login credentials are needed'],
    429: ['Rate limiting detected', 'Add delays between requests', 'Consider using proxies'],
    500: ['Server error on target website', 'Try again later', 'Check website status'],
    502: ['Bad gateway error', 'Target server may be down', 'Check website availability'],
    503: ['Service unavailable', 'Website may be under maintenance', 'Try again later']
  };

  return recommendations[statusCode] || ['Unknown error', 'Check network connectivity', 'Review request configuration'];
}

return [{ json: {
  errorAnalysis,
  testSessionId,
  status: 'FAILED',
  errorTimestamp: new Date().toISOString(),
  frameworkVersion: 'testing-framework-v1.0',
  validationResults: validationResults,
  processingTime: errorProcessingTime,
  enrichedDataReceived: !!enrichedResponse.testSessionId
}}];
